import {yupResolver} from '@hookform/resolvers/yup';
import {Datepicker, Dropdown, Input, Modal, Typography} from 'client-library';
import React, {useEffect, useMemo, useState} from 'react';
import {Controller, useForm} from 'react-hook-form';
import * as yup from 'yup';
import {
  applicationStatusOptions,
  applicationTypeOptions,
  evaluationTypeOptions,
} from '../../screens/jobTenders/constants';
import useJobTenderApplicationsInsert from '../../services/graphql/jobTenders/useJobTenderApplicationInsert';
import useBasicInfoGet from '../../services/graphql/userProfile/basicInfo/useBasicInfoGet';
import useUserProfiles from '../../services/graphql/userProfile/useUserProfiles';
import {DropdownDataString} from '../../types/dropdownData';
import {JobTenderApplication, JobTenderApplicationInsertParams} from '../../types/graphql/jobTenders';
import {MicroserviceProps} from '../../types/micro-service-props';
import {ScreenProps} from '../../types/screen-props';
import {parseDateForBackend, parseToDate} from '../../utils/dateUtils';
import {
  ConfirmModalContent,
  ModalContentWrapper,
  Row,
  RowFullWidth,
  TriangleIcon,
} from '../JobTenderApplicationModal/styles';

const tenderApplicationSchema = yup.object().shape({
  type: yup
    .object()
    .default(undefined)
    .shape({id: yup.string().required(), title: yup.string().required()})
    .required('Ovo polje je obavezno'),
  status: yup
    .object()
    .default(undefined)
    .shape({id: yup.string().required(), title: yup.string().required()})
    .required('Ovo polje je obavezno'),
  date_of_application: yup.date().required('Ovo polje je obavezno').nullable(),
  first_name: yup.string().required('Ovo polje je obavezno'),
  last_name: yup.string().required('Ovo polje je obavezno'),
  official_personal_id: yup.string().required('Ovo polje je obavezno'),
  date_of_birth: yup
    .date()
    .required('Ovo polje je obavezno')
    .test('more than 18 year old', 'Kandidat mora biti punoljetan', value => {
      if (!value) return false;
      const today = new Date();
      const birthDate = new Date(value);
      const age = today.getFullYear() - birthDate.getFullYear();
      return age >= 18;
    }),
  citizenship: yup
    .object()
    .default(undefined)
    .shape({id: yup.string(), title: yup.string()})
    .required('Ovo polje je obavezno'),
  user_profile: yup
    .object()
    .default(undefined)
    .shape({id: yup.number(), title: yup.string()})
    .when('type', {
      is: (value: DropdownDataString) => value && value.id === 'internal',
      then: schema => schema.required('Ovo polje je obavezno'),
      otherwise: schema => schema.optional(),
    }),
  evaluation: yup.object(),
});

export interface JobTenderApplicationModalModalProps extends ScreenProps {
  selectedItem?: JobTenderApplication;
  open: boolean;
  onClose: () => void;
  refetchList: () => void;
  countries?: any[];
  jobTenderId: number;
  alert: any;
  context: MicroserviceProps;
}

export const JobTenderApplicationModal: React.FC<JobTenderApplicationModalModalProps> = ({
  selectedItem,
  open,
  onClose,
  refetchList,
  countries,
  jobTenderId,
  alert,
  context,
}) => {
  const [selectedUserId, setSelectedIdUser] = useState<number>(0);
  const [confirmationModal, setConfirmationModal] = useState<boolean>(false);

  const {data: userData} = useBasicInfoGet(selectedUserId);

  const {userProfiles} = useUserProfiles({page: 1, size: 1000});

  const citizenshipArray = useMemo(() => {
    return countries?.map(country => {
      return {
        id: country.alpha3,
        title: country.name,
      };
    });
  }, [countries]);

  const {
    register,
    handleSubmit,
    control,
    formState: {errors},
    reset,
    watch,
    setValue,
  } = useForm({
    defaultValues: {type: {id: 'internal', title: 'Interni'}},
    resolver: yupResolver(tenderApplicationSchema),
  });

  const {status, type, user_profile} = watch();

  const {mutate, loading: isSaving} = useJobTenderApplicationsInsert();

  const onSubmit = (values: any) => {
    if (isSaving) return;

    const data: JobTenderApplicationInsertParams = {
      type: values.type.id,
      date_of_application: parseDateForBackend(values?.date_of_application) ?? '',
      status: values?.status?.title,
      job_tender_id: jobTenderId,
      active: true,
    };

    if (values?.id) data.id = values?.id;
    if (data.type === 'external') {
      // data.evaluation = values?.evaluation?.id;
      data.first_name = values.first_name;
      data.last_name = values.last_name;
      data.citizenship = values?.citizenship?.id;
      data.date_of_birth = parseDateForBackend(values?.date_of_birth) ?? '';
      data.official_personal_id = values?.official_personal_id;
    } else {
      data.user_profile_id = values?.user_profile?.id;
    }

    try {
      mutate(
        data,
        () => {
          context.alert.success('Uspješno sačuvano.');
          refetchList();
          onClose();
          reset();
          if (data.status === 'Izabran' && data.type === 'external') {
            navigateToUserCreation();
          }
        },
        () => {
          context.alert.error('Greška. Promjene nisu sačuvane.');
          onClose();
          reset();
        },
      );
    } catch (e) {
      console.log(e);
    }
  };

  const userOptions = useMemo(
    () =>
      userProfiles && userProfiles
        ? [...userProfiles.map(item => ({...item, title: `${item.first_name} ${item.last_name}`}))]
        : [],
    [userProfiles],
  );

  const toggleConfirmModal = () => {
    setConfirmationModal(prev => !prev);
  };

  const navigateToUserCreation = () => {
    const {first_name, last_name, date_of_birth, evaluation, citizenship, official_personal_id} = watch();

    context.navigation.navigate('/hr/employees/add-new', {
      state: {user: {first_name, last_name, date_of_birth, evaluation, citizenship, official_personal_id}},
    });
  };

  // Choosing an internal user to populate the corresponding fields.
  useEffect(() => {
    if (userData) {
      reset({
        ...watch(),
        first_name: userData.first_name,
        last_name: userData.last_name,
        official_personal_id: userData.official_personal_id,
        date_of_birth: parseToDate(userData.date_of_birth) ?? undefined,
        citizenship: citizenshipArray?.find(c => (c.id = userData.citizenship)) || undefined,
        user_profile: {id: userData.id, title: `${userData.first_name} ${userData.last_name}`},
      });
    }
  }, [userData]);

  useEffect(() => {
    if (selectedItem) {
      reset({
        ...selectedItem,
        date_of_application: parseToDate(selectedItem?.date_of_application),
        date_of_birth: parseToDate(selectedItem?.date_of_birth) ?? undefined,
        status: selectedItem?.status
          ? applicationStatusOptions.find(st => st.title === selectedItem?.status)
          : undefined,
        type: selectedItem?.type ? applicationTypeOptions.find(st => st.id === selectedItem?.type) : undefined,
        evaluation: selectedItem?.evaluation
          ? evaluationTypeOptions.find(st => st.id === selectedItem?.evaluation)
          : undefined,
        citizenship: selectedItem?.citizenship
          ? citizenshipArray?.find(st => st.id === selectedItem.citizenship)
          : undefined,
      });
    }
  }, [selectedItem]);

  useEffect(() => {
    if (status?.id === 'accepted' && type?.id === 'external') {
      setConfirmationModal(true);
    }
  }, [status]);

  useEffect(() => {
    if (type?.id === 'external') {
      setValue('user_profile', undefined as any);
      setSelectedIdUser(0);
    }
  }, [type]);

  useEffect(() => {
    if (user_profile) {
      const item = userOptions.find(user => user.id === user_profile.id);

      if (item) {
        setSelectedIdUser(item.id);
      }
    }
  }, [user_profile]);

  const countryOptions = useMemo(() => {
    return countries?.map(country => {
      return {
        id: country.alpha3,
        title: country.name,
      };
    });
  }, [countries]);
  return (
    <>
      <Modal
        open={open}
        onClose={onClose}
        leftButtonText="Otkaži"
        rightButtonText="Sačuvaj"
        rightButtonOnClick={handleSubmit(onSubmit)}
        buttonLoading={isSaving}
        content={
          <ModalContentWrapper>
            <Row>
              <Controller
                name="type"
                control={control}
                render={({field: {name, value, onChange}}) => (
                  <Dropdown
                    label="TIP KANDIDATA:"
                    name={name}
                    options={applicationTypeOptions}
                    value={value}
                    onChange={onChange}
                    error={errors.type?.message}
                    isDisabled={!!selectedItem?.id}
                  />
                )}
              />
              <Controller
                name="user_profile"
                control={control}
                render={({field: {name, value, onChange}}) => (
                  <Dropdown
                    label="PRETRAGA:"
                    name={name}
                    options={userOptions}
                    isSearchable={true}
                    value={value as any}
                    onChange={onChange}
                    isDisabled={!!selectedItem?.id || type?.id === 'external'}
                    error={errors.user_profile?.message}
                    placeholder="Unesite ime"
                  />
                )}
              />
            </Row>
            <Row>
              <Input
                {...register('first_name', {required: 'Ovo polje je obavezno'})}
                label="IME:"
                disabled={type?.id === 'internal'}
                error={errors.first_name?.message as string}
              />

              <Input
                {...register('last_name', {required: 'Ovo polje je obavezno'})}
                label="PREZIME:"
                disabled={type?.id === 'internal'}
                error={errors.last_name?.message as string}
              />
            </Row>
            <Row>
              <Input
                {...register('official_personal_id', {required: 'Ovo polje je obavezno'})}
                label="BROJ LIČNE KARTE:"
                error={errors.official_personal_id?.message as string}
                disabled={type?.id === 'internal'}
              />

              <Controller
                name="date_of_birth"
                control={control}
                rules={{required: 'Ovo polje je obavezno'}}
                render={({field: {onChange, name, value}}) => (
                  <Datepicker
                    onChange={onChange}
                    label="DATUM ROĐENJA:"
                    name={name}
                    selected={value}
                    error={errors.date_of_birth?.message as string}
                    disabled={type?.id === 'internal'}
                  />
                )}
              />
            </Row>
            <RowFullWidth>
              <Controller
                name="citizenship"
                rules={type?.id !== 'internal' ? {required: 'Ovo polje je obavezno'} : {}}
                control={control}
                render={({field: {onChange, name, value}}) => {
                  return (
                    <Dropdown
                      onChange={onChange}
                      value={value as any}
                      name={name}
                      style={{width: '100%'}}
                      label="DRŽAVLJANSTVO:"
                      options={countryOptions || []}
                      error={errors.citizenship?.message as string}
                      isDisabled={type?.id === 'internal'}
                      isSearchable
                    />
                  );
                }}
              />
            </RowFullWidth>
            <RowFullWidth>
              <Controller
                name="evaluation"
                control={control}
                render={({field: {onChange, name, value}}) => {
                  return (
                    <Dropdown
                      onChange={onChange}
                      value={value as any}
                      name={name}
                      style={{width: '100%'}}
                      label="OCJENA:"
                      options={evaluationTypeOptions || []}
                      isDisabled
                    />
                  );
                }}
              />
            </RowFullWidth>
            <Row>
              <Controller
                name="date_of_application"
                control={control}
                rules={{required: 'Ovo polje je obavezno'}}
                render={({field: {onChange, name, value}}) => (
                  <Datepicker
                    onChange={onChange}
                    label="DATUM PRIJAVE:"
                    name={name}
                    selected={value}
                    error={errors.date_of_application?.message as string}
                  />
                )}
              />
              <Controller
                name="status"
                rules={{required: 'Ovo polje je obavezno'}}
                control={control}
                render={({field: {onChange, name, value}}) => {
                  return (
                    <Dropdown
                      onChange={onChange}
                      value={value as any}
                      name={name}
                      label="STATUS:"
                      options={applicationStatusOptions || []}
                      error={errors.status?.message as string}
                      placeholder="Odaberite status"
                    />
                  );
                }}
              />
            </Row>
          </ModalContentWrapper>
        }
        title={'DODAJTE NOVOG KANDIDATA'}
      />
      <Modal
        open={confirmationModal}
        onClose={toggleConfirmModal}
        width={450}
        leftButtonText="Otkaži"
        rightButtonText="Nastavi"
        leftButtonOnClick={toggleConfirmModal}
        rightButtonOnClick={handleSubmit(onSubmit)}
        content={
          <ConfirmModalContent>
            <TriangleIcon />
            <Typography
              content="Da li želite kreirati korisnički profil za ovog kandidata?"
              variant="bodyLarge"
              style={{fontWeight: 600}}
            />
            <Typography
              content={'Ukoliko odaberete opciju nastavi, bićete upućeni na stranicu za dodavanje novog zaposlenog.'}
              variant="bodySmall"
              style={{textAlign: 'center'}}
            />
          </ConfirmModalContent>
        }
      />
    </>
  );
};

import {Datepicker, Dropdown, FileUpload, Input, Modal, Typography} from 'client-library';
import React, {useEffect, useMemo, useState} from 'react';
import {Controller, useForm} from 'react-hook-form';
import {AbsentsModalProps} from '../../screens/employees/absents/types';
import useOrganizationUnits from '../../services/graphql/organizationUnits/useOrganizationUnits';
import useAbsentInsert from '../../services/graphql/userProfile/absents/useAbsentInsert';
import {AbsentType, UserProfileAbsentsParams} from '../../types/graphql/profileAbsentsTypes';
import {dropdownOptions, dropdownVacationOptions} from './constants';
import {FileUploadWrapper, FormGroup, ModalContentWrapper, UploadedFileContainer, UploadedFileWrapper} from './styles';
import {parseDate, parseDateForBackend, parseToDate} from '../../utils/dateUtils';

const initialValues: UserProfileAbsentsParams = {
  id: null,
  user_profile_id: 0,
  absent_type_id: null,
  target_organization_unit_id: null,
  date_of_start: null,
  date_of_end: null,
  description: '',
  file_id: null,
};

export const AbsentModal: React.FC<AbsentsModalProps> = ({
  selectedItem,
  absentTypes,
  open,
  onClose,
  userProfileId,
  alert,
}) => {
  const [uploadedFiles, setUploadedFiles] = useState<File[]>([]);
  const [absentChildType, setAbsentChildType] = useState<AbsentType[]>([]);
  const [isVacation, setIsVacation] = useState<boolean | null>(null);
  const [selectedAbsentTypeId, setSelectedAbsentTypeId] = useState(null);

  const handleUpload = (files: FileList) => {
    const fileList = Array.from(files);
    setUploadedFiles(fileList);
  };

  const handleTypeChange = (selectedValue: any) => {
    if (selectedValue.id === 1) {
      setIsVacation(true);
      setAbsentChildType([...absentTypes.filter(item => item.accounting_days_off)]);
    } else {
      setIsVacation(false);
      setAbsentChildType([...absentTypes.filter(item => !item.accounting_days_off)]);
    }
  };

  const {organizationUnits} = useOrganizationUnits();

  const organizationUnitsList = useMemo(() => {
    return organizationUnits
      .filter(i => !i.parent_id)
      .map(unit => {
        return {id: unit.id, title: unit.title};
      });
  }, [organizationUnits]);

  const {mutate, loading: isSaving} = useAbsentInsert();

  const handleSave = (values: any) => {
    if (isSaving) return;
    const payload = {
      id: values?.id || 0,
      user_profile_id: userProfileId,
      date_of_start: parseDateForBackend(values?.date_of_start),
      date_of_end: parseDateForBackend(values?.date_of_end),
      absent_type_id: isVacation ? absentChildType[0]?.id : values?.absent_type?.id,
      description: values?.description,
      target_organization_unit_id: values?.target_organization_unit?.id || null,
      file_id: values?.file_id || null,
    };

    mutate(
      payload,
      () => {
        onClose(true);
        alert.success('Uspješno sačuvano.');
        reset(initialValues);
      },
      () => {
        alert.error('Greška. Promjene nisu sačuvane.');
      },
    );
  };

  const {
    register,
    handleSubmit,
    control,
    formState: {errors},
    reset,
  } = useForm<UserProfileAbsentsParams>({defaultValues: initialValues});

  useEffect(() => {
    if (selectedItem) {
      reset({
        ...selectedItem,
        date_of_end: parseToDate(selectedItem.date_of_end),
        date_of_start: parseToDate(selectedItem?.date_of_start),
      });
      if (selectedItem.id !== 0) {
        if (selectedItem?.absent_type.accounting_days_off) {
          setAbsentChildType([...absentTypes.filter(item => item.accounting_days_off)]);
          setIsVacation(true);
        } else {
          setAbsentChildType([...absentTypes.filter(item => !item.accounting_days_off)]);
          handleAbsentTypeChange(selectedItem?.absent_type.title);
          setIsVacation(false);
        }
      }
    }
  }, [selectedItem, reset]);

  const handleAbsentTypeChange = (selectedValue: any) => {
    setSelectedAbsentTypeId(selectedValue);
  };

  const VacationValue = () => {
    if (isVacation) {
      return dropdownOptions[1];
    } else if (isVacation === false) {
      return dropdownOptions[2];
    } else {
      return dropdownOptions[0];
    }
  };

  return (
    <Modal
      open={open}
      onClose={() => {
        onClose();
        reset(initialValues);
      }}
      leftButtonText="Otkaži"
      rightButtonText="Sačuvaj"
      rightButtonOnClick={handleSubmit(handleSave)}
      buttonLoading={isSaving}
      content={
        <ModalContentWrapper>
          <FormGroup>
            <Dropdown
              label="VRSTA ZAHTJEVA:"
              options={dropdownOptions.slice(1)}
              value={VacationValue() as any}
              onChange={handleTypeChange}
              placeholder="Birajte vrstu"
              name="vacation"
            />
          </FormGroup>
          {!isVacation && (
            <FormGroup>
              <Controller
                name="absent_type"
                control={control}
                rules={{required: 'Ovo polje je obavezno'}}
                render={({field: {onChange, name, value}}) => (
                  <Dropdown
                    label="VRSTA:"
                    name={name}
                    options={absentChildType}
                    value={value as any}
                    onChange={selectedValue => {
                      handleAbsentTypeChange(selectedValue.title);
                      onChange(selectedValue);
                    }}
                    error={errors.absent_type?.message}
                    placeholder="Birajte vrstu"
                  />
                )}
              />
            </FormGroup>
          )}
          {!isVacation && selectedAbsentTypeId === 'Upućivanje u drugi državni organ' && (
            <FormGroup>
              <Controller
                name="target_organization_unit"
                control={control}
                render={({field: {onChange, name, value}}) => (
                  <Dropdown
                    label="DRŽAVNI ORGAN:"
                    name={name}
                    options={organizationUnitsList as any}
                    value={value as any}
                    onChange={onChange}
                    error={errors.target_organization_unit?.message}
                    placeholder="Birajte državni organ"
                  />
                )}
              />
            </FormGroup>
          )}
          <FormGroup>
            <Controller
              name="date_of_start"
              control={control}
              rules={{required: 'Ovo polje je obavezno'}}
              render={({field: {onChange, name, value}}) => (
                <Datepicker
                  onChange={onChange}
                  label="POČETAK TRAJANJA:"
                  name={name}
                  selected={value}
                  error={errors.date_of_start?.message as string}
                />
              )}
            />
          </FormGroup>
          <FormGroup>
            <Controller
              name="date_of_end"
              control={control}
              rules={{required: 'Ovo polje je obavezno'}}
              render={({field: {onChange, name, value}}) => (
                <Datepicker
                  onChange={onChange}
                  label="KRAJ TRAJANJA (Uključujuci taj dan):"
                  name={name}
                  selected={value}
                  error={errors.date_of_end?.message as string}
                />
              )}
            />
          </FormGroup>

          <FormGroup>
            <Input {...register('description')} label="OPIS:" placeholder="Unesite opis..." textarea />
          </FormGroup>
          <FileUploadWrapper>
            <FileUpload
              icon={<></>}
              style={{width: '100%'}}
              variant="secondary"
              onUpload={handleUpload}
              note={<Typography variant="bodySmall" content="Validacija" />}
              buttonText="Učitaj"
            />
          </FileUploadWrapper>
          {uploadedFiles.length > 0 && (
            <UploadedFileWrapper>
              {uploadedFiles.map((file, index) => (
                <UploadedFileContainer key={index}>
                  <Typography variant="bodySmall" content={file.name} />
                </UploadedFileContainer>
              ))}
            </UploadedFileWrapper>
          )}
        </ModalContentWrapper>
      }
      title={'ZAHTJEVI'}
    />
  );
};

import {yupResolver} from '@hookform/resolvers/yup';
import {Datepicker, Dropdown, FileUpload, Input, Modal, Typography} from 'client-library';
import React, {useEffect, useMemo, useState} from 'react';
import {Controller, useForm} from 'react-hook-form';
import * as yup from 'yup';
import {ConfirmationsModalProps} from '../../screens/employees/confirmations/types';
import useResolutionInsert from '../../services/graphql/userProfile/resolution/useResolutionInsert';
import {DropdownDataNumber} from '../../types/dropdownData';
import {parseDateForBackend, parseToDate} from '../../utils/dateUtils';
import {FileUploadWrapper, FormGroup, ModalContentWrapper, UploadedFileContainer, UploadedFileWrapper} from './styles';
import useSettingsDropdownOverview from '../../services/graphql/settingsDropdown/useSettingsDropdownOverview';
import {educationTypes, resolutionTypes} from '../education/modals/constants';

const confirmationSchema = yup.object().shape({
  resolution_purpose: yup.string(),
  date_of_start: yup.date().required('Ovo polje je obavezno'),
  resolution_type: yup.object().required('Ovo polje je obavezno'),
});

export const ConfirmationsModal: React.FC<ConfirmationsModalProps> = ({
  selectedItem,
  open,
  onClose,
  userProfileId,
  alert,
}) => {
  const {
    register,
    handleSubmit,
    control,
    formState: {errors},
    reset,
  } = useForm({
    resolver: yupResolver(confirmationSchema),
  });

  const {data: types} = useSettingsDropdownOverview({entity: resolutionTypes.resolution_types});

  const resolutionTypesOptions = useMemo(() => {
    return types?.slice(1, 3).filter(type => ({id: type?.id as number, title: type?.title} || []));
  }, [types]);

  const [uploadedFiles, setUploadedFiles] = useState<File[]>([]);

  const handleUpload = (files: FileList) => {
    const fileList = Array.from(files);
    setUploadedFiles(fileList);
  };

  const {mutate: saveUserProfileResolution, loading: isSaving} = useResolutionInsert();

  const handleSave = (value: any) => {
    if (isSaving) return;

    const payload = {
      ...value,
      id: value?.id || 0,
      user_profile_id: Number(userProfileId),
      date_of_start: parseDateForBackend(value?.date_of_start),
      date_of_end: null,
      file_id: value?.file_id || 0,
      resolution_purpose: value?.resolution_purpose || '',
      resolution_type_id: value?.resolution_type.id || null,
    };

    delete payload.created_at;
    delete payload.updated_at;
    delete payload.resolution_type;
    delete payload.user_profile;

    saveUserProfileResolution(
      payload,
      () => {
        onClose(true);
        alert.success('Uspješno sačuvano.');
      },
      () => {
        onClose(true);
        alert.error('Greška. Promjene nisu sačuvane.');
      },
    );
  };

  useEffect(() => {
    if (selectedItem) {
      reset({
        ...selectedItem,
        date_of_end: parseToDate(selectedItem?.date_of_end),
        date_of_start: parseToDate(selectedItem?.date_of_start),
      });
    }
  }, [selectedItem]);

  return (
    <Modal
      open={open}
      onClose={() => {
        reset();
        onClose();
      }}
      leftButtonText="Otkaži"
      rightButtonText="Sačuvaj"
      rightButtonOnClick={handleSubmit(handleSave)}
      buttonLoading={isSaving}
      content={
        <ModalContentWrapper>
          <FormGroup>
            {resolutionTypesOptions && (
              <Controller
                name="resolution_type"
                control={control}
