  navigation,
  onPageChange,
  data,
  search,
  filters,
  onFilterChange,
  onSearch,
  parentRef,
  loading,
}) => {
  const overviewRef = useRef<HTMLDivElement>(null);
  const state = navigation.location.state;
  const isDetails = navigation.location.pathname.split('/').length === 6;

  const {organizationUnits} = useOrganizationUnits(undefined, true);
  const {data: jobPositions} = useJobPositions('');

  const organizationUnitsList = useMemo(() => {
    return organizationUnits
      ? [
        {id: 0, title: 'Sve organizacione jedinice'},
        ...organizationUnits.map(unit => {
          return {id: unit.id, title: unit.title};
        }),
      ]
      : [];
  }, [organizationUnits]);

  const userProfileList = data.items
    ? data?.items?.map((item: UserProfile) => ({
      full_name: `${item.first_name} ${item.last_name}`,
      ...item,
      active: item.active ? 'Aktivan' : 'Neaktivan',
    }))
    : [];

  const jobPositionOptions = [
    {id: 0, title: 'Sva radna mjesta'},
    ...(jobPositions?.items
      ? jobPositions.items.map((jobPosition: any) => ({id: jobPosition.id, title: jobPosition.title}))
      : []),
  ];

  useEffect(() => {
    if (state?.scroll || isDetails) {
      scrollToTheNextElement(parentRef, overviewRef);
    }
  }, [isDetails]);

  return (
    <OverviewBox ref={overviewRef}>
      <MainTitle variant="bodyMedium" content="PREGLED SVIH ZAPOSLENIH" />
      <Divider color={Theme?.palette?.gray200} height="1px" />
      <Header>
        <Filters>
          <FilterDropdown
            label="FILTER ORGANIZACIONIH JEDINICA:"
            options={organizationUnitsList}
            onChange={value => onFilterChange(value, 'organization_unit_id')}
            value={filters.organization_unit_id}
            name="organization_unit_id"
            placeholder="Odaberite organizacionu jedinicu"
          />

          <FilterDropdown
            label="RADNO MJESTO:"
            options={jobPositionOptions}
            onChange={value => onFilterChange(value, 'job_position_id')}
            value={filters.job_position_id}
            name="job_position_id"
            placeholder="Odaberite radno mjesto"
          />

          <FilterDropdown
            label="STATUS:"
            options={statusOptions as any}
            onChange={value => onFilterChange(value, 'is_active')}
            value={filters.is_active}
            name="is_active"
            placeholder="Odaberite status"
          />

          <FilterInput
            onChange={onSearch}
            label="PRETRAGA PO IMENU I PREZIMENU:"
            value={search}
            rightContent={<SearchIcon />}
            placeholder="Unesite ime i prezime"
          />
        </Filters>
        <Controls>
          <Button
            content="Dodaj zaposlenog"
            variant="secondary"
            style={{width: 170}}
            onClick={() => {
              navigation.navigate('/hr/employees/add-new');
            }}
          />
        </Controls>
      </Header>
      <Table
        tableHeads={tableHeads}
        data={userProfileList}
        style={{marginBottom: 22}}
        isLoading={loading}
        onRowClick={row => {
          navigation.navigate(`/hr/employees/details/${row.id}/basic-info`);
          scrollToTheNextElement(parentRef, overviewRef);
        }}
      />
      <Pagination
        pageCount={Math.ceil(data.total / 10)}
        onChange={onPageChange}
        variant="filled"
        itemsPerPage={2}
        pageRangeDisplayed={3}
      />
    </OverviewBox>
  );
};

export default EmployeesList;

import {yupResolver} from '@hookform/resolvers/yup';
import {Datepicker, Dropdown, FileUpload, Modal, Typography} from 'client-library';
import React, {useEffect, useState} from 'react';
import {Controller, useForm} from 'react-hook-form';
import * as yup from 'yup';
import {yesOrNoOptionsString} from '../../constants';
import {EvaluationModalProps} from '../../screens/employees/evaluations/types';
import useEvaluationInsert from '../../services/graphql/userProfile/evaluation/useEvaluationInsert';
import {DropdownDataNumber} from '../../types/dropdownData';
import {parseDateForBackend, parseToDate} from '../../utils/dateUtils';
import {FileUploadWrapper, FormWrapper, Row} from './styles';

const evaluationSchema = yup.object().shape({
  date_of_evaluation: yup.date().required('Ovo polje je obavezno'),
  is_relevant: yup
    .object()
    .default(undefined)
    .shape({id: yup.string(), title: yup.string()})
    .required('Ovo polje je obavezno'),
  score: yup
    .object()
    .default(undefined)
    .shape({id: yup.string(), title: yup.string()})
    .required('Ovo polje je obavezno'),
  user_profile_id: yup.number().required('Ovo polje je obavezno'),
});

export const EvaluationModal: React.FC<EvaluationModalProps> = ({
  alert,
  refetchList,
  selectedItem,
  open,
  onClose,
  userProfileId,
  evaluationTypes,
}) => {
  const [evaluationTypesOption, setEvaluationTypesOption] = useState<DropdownDataNumber[]>([]);

  const {
    handleSubmit,
    control,
    formState: {errors},
    reset,
  } = useForm({
    resolver: yupResolver(evaluationSchema),
    defaultValues: {user_profile_id: Number(userProfileId) ?? null},
  });

  useEffect(() => {
    if (Array.isArray(evaluationTypes) && evaluationTypes.length > 0) {
      const newData = evaluationTypes.map(item => ({id: item.id || 0, title: item.title || ''}));
      setEvaluationTypesOption(newData);
    }
  }, [evaluationTypes]);

  const {mutate, loading: isSaving} = useEvaluationInsert();

  const onSubmit = async (data: any) => {
    if (isSaving) return;

    const payload: any = {
      user_profile_id: data?.user_profile_id,
      score: data?.score.title,
      is_relevant: data?.is_relevant?.id === 'Da' ? true : false,
      date_of_evaluation: parseDateForBackend(data?.date_of_evaluation),
      file_id: data?.file_id,
      evaluation_type_id: data?.score.id,
      evaluator: '',
    };

    if (selectedItem) {
      payload.id = selectedItem.id ? selectedItem.id : 0;
    }

    await mutate(
      payload,
      () => {
        refetchList();
        alert.success('Uspješno sačuvano.');
        onClose();
        reset();
      },
      () => {
        alert.error('Greška. Promjene nisu sačuvane.');
      },
    );
  };

  useEffect(() => {
    if (selectedItem) {
      reset({
        ...selectedItem,
        is_relevant: {id: selectedItem?.is_relevant ? 'Da' : 'Ne', title: selectedItem?.is_relevant ? 'Da' : 'Ne'},
        date_of_evaluation: parseToDate(selectedItem?.date_of_evaluation),
        score: {id: selectedItem?.evaluation_type.id, title: selectedItem?.evaluation_type.title},
        user_profile_id: Number(userProfileId),
      });
    }
  }, [selectedItem]);

  return (
    <Modal
      open={open}
      onClose={() => {
        onClose();
        reset();
      }}
      leftButtonText="Otkaži"
      rightButtonText="Sačuvaj"
      rightButtonOnClick={handleSubmit(onSubmit)}
      buttonLoading={isSaving}
      content={
        <FormWrapper>
          <Row>
            <Controller
              name="date_of_evaluation"
              control={control}
              render={({field: {onChange, name, value}}) => (
                <Datepicker
                  onChange={onChange}
                  selected={value ? new Date(value) : ''}
                  name={name}
                  label="DATUM:"
                  error={errors.date_of_evaluation?.message}
                />
              )}
            />
            <Controller
              name="is_relevant"
              control={control}
              render={({field: {onChange, name, value}}) => {
                return (
                  <Dropdown
                    onChange={onChange}
                    value={value as any}
                    name={name}
                    label="PRAVOSNAŽNOST:"
                    options={yesOrNoOptionsString}
                    error={errors.is_relevant?.message}
                  />
                );
              }}
            />
          </Row>
          <Row>
            <Controller
              name="score"
              control={control}
              render={({field: {onChange, name, value}}) => {
                return (
                  <Dropdown
                    onChange={onChange}
                    value={value as any}
                    name={name}
                    label="OCJENA:"
                    options={evaluationTypesOption}
                    error={errors.is_relevant?.message}
                  />
                );
              }}
            />
          </Row>

          <FileUploadWrapper>
            <FileUpload
              icon={<></>}
              style={{width: '100%'}}
              variant="secondary"
              onUpload={(item: File | FileList) => console.log(item)}
              note={<Typography variant="bodySmall" content="Validacija" />}
              buttonText="Učitaj"
            />
          </FileUploadWrapper>
        </FormWrapper>
      }
      title={'DODAJTE NOVO ZAPOSLENJE'}
    />
  );
};

import {yupResolver} from '@hookform/resolvers/yup';
import {Datepicker, Dropdown, FileUpload, Input, Modal, Typography} from 'client-library';
import React, {useEffect} from 'react';
import {Controller, useForm} from 'react-hook-form';
import * as yup from 'yup';
import {yesOrNoOptionsString} from '../../constants';
import {ExperienceModalProps} from '../../screens/employees/experience/types';
import {formatData} from '../../screens/employees/experience/utils';
import useOrganizationUnits from '../../services/graphql/organizationUnits/useOrganizationUnits';
import useExperienceInsert from '../../services/graphql/userProfile/experience/useExperienceInsert';
import {DropdownDataString} from '../../types/dropdownData';
import {calculateExperience, parseToDate} from '../../utils/dateUtils';
import {FileUploadWrapper, FormWrapper, Row} from './styles';

const experienceSchema = yup.object().shape({
  relevant: yup
    .object()
    .default(undefined)
    .required('Ovo polje je obavezno')
    .shape({id: yup.string().required(), title: yup.string().required()}),
  amount_of_experience: yup.number().required('Ovo polje je obavezno'),
  amount_of_insured_experience: yup
    .number()
    .transform(value => (!value || isNaN(value) ? null : value))
    .nullable()
    .required('Ovo polje je obavezno'),
  date_of_end: yup
    .date()
    .required('Ovo polje je obavezno')
    .min(yup.ref('date_of_start'), 'Kraj radnog odnosa ne može biti prije početka radnog odnosa.')
    .nullable(),
  date_of_start: yup.date().required('Ovo polje je obavezno').nullable(),
  organization_unit: yup.string().when('relevant', {
    is: (value: DropdownDataString) => value && value.id === 'Ne',
    then: schema => schema.required('Ovo polje je obavezno'),
    otherwise: schema => schema.optional(),
  }),
  organization_unit_id: yup
    .object()
    .default(undefined)
    .shape({id: yup.string().required(), title: yup.string().required()})
    .when('relevant', {
      is: (value: DropdownDataString) => value && value.id === 'Da',
      then: schema => schema.required('Ovo polje je obavezno'),
      otherwise: schema => schema.optional(),
    }),
  user_profile_id: yup.number().required(),
});

export const ExperienceModal: React.FC<ExperienceModalProps> = ({
  refetchList,
  selectedItem,
  open,
  onClose,
  units,
  userProfileId,
  alert,
}) => {
  const {
    register,
    handleSubmit,
    control,
    watch,
    formState: {errors},
    reset,
    setValue,
  } = useForm({resolver: yupResolver(experienceSchema), defaultValues: {user_profile_id: userProfileId}});

  const {mutate, loading: isSaving} = useExperienceInsert();
  const {organizationUnitsList} = useOrganizationUnits();

  const {relevant, date_of_start, date_of_end, organization_unit, organization_unit_id} = watch();
  console.log(selectedItem);

  useEffect(() => {
    if (selectedItem) {
      reset({
        ...selectedItem,
        relevant: {id: selectedItem?.relevant ? 'Da' : 'Ne', title: selectedItem?.relevant ? 'Da' : 'Ne'},
        organization_unit_id: organizationUnitsList.find(orgUnit => orgUnit.id === selectedItem?.organization_unit_id),
        date_of_start: parseToDate(selectedItem?.date_of_start),
        date_of_end: parseToDate(selectedItem?.date_of_end),
        user_profile_id: userProfileId,
      });
    }
  }, [selectedItem]);

  const onSubmit = async (data: any) => {
    if (isSaving) return;

    const payload = formatData(data, !selectedItem);

    try {
      await mutate(
        payload,
        () => {
          alert.success('Uspješno sačuvano.');
          refetchList && refetchList();
          onClose();
        },
        () => {
          alert.error('Greška. Promjene nisu sačuvane.');
        },
      );
    } catch (e) {
      console.log(e);
    }
  };

  const isOrgUnitDisabled = relevant?.id === 'Da' || !relevant;

  useEffect(() => {
    if (date_of_end && date_of_start) {
      const calculatedExperience = calculateExperience(date_of_start, date_of_end);
      setValue('amount_of_experience', calculatedExperience);
    }

    if (organization_unit && isOrgUnitDisabled) {
      setValue('organization_unit', '');
    }

    if (organization_unit_id && !isOrgUnitDisabled) {
      setValue('organization_unit_id', {id: '', title: ''});
    }
  }, [date_of_end, date_of_start, isOrgUnitDisabled]);

  return (
    <Modal
      open={open}
      onClose={() => {
        onClose();
        reset();
      }}
      leftButtonText="Otkaži"
      rightButtonText="Sačuvaj"
      rightButtonOnClick={handleSubmit(onSubmit)}
      buttonLoading={isSaving}
      content={
        <FormWrapper>
          <Row>
            <Controller
              name="relevant"
              control={control}
              render={({field: {onChange, name, value}}) => {
                return (
                  <Dropdown
                    onChange={onChange}
                    value={value as any}
                    name={name}
                    label="SUDSTVO:"
                    options={yesOrNoOptionsString}
                    error={errors.relevant?.message}
                  />
                );
              }}
            />
            <Controller
              name="date_of_start"
              control={control}
              render={({field: {onChange, name, value}}) => (
                <Datepicker
                  onChange={onChange}
                  label="POČETAK RADNOG ODNOSA:"
                  name={name}
                  selected={value}
                  error={errors.date_of_start?.message}
                />
              )}
            />
          </Row>
          <Row>
            <Controller
              name="organization_unit_id"
              control={control}
              render={({field: {onChange, name, value}}) => {
                return (
                  <Dropdown
                    onChange={onChange}
                    value={value as any}
                    name={name}
                    label="JEDINICA:"
                    options={units}
                    isDisabled={!isOrgUnitDisabled}
                    error={errors.organization_unit_id?.message}
                  />
                );
              }}
            />
            <Controller
              name="date_of_end"
              control={control}
              render={({field: {onChange, name, value}}) => (
                <Datepicker
                  onChange={onChange}
                  label="KRAJ RADNOG ODNOSA:"
                  name={name}
                  selected={value}
                  error={errors.date_of_end?.message}
                />
              )}
            />
          </Row>
          <Row>
            <Input
              {...register('organization_unit')}
              label="ORGANIZACIJA/INSTITUCIJA:"
              error={errors.organization_unit?.message}
              disabled={isOrgUnitDisabled}
            />
            <Input {...register('amount_of_insured_experience')} label="PRIJAVLJENI STAŽ (MJESECI):" type="number" />
          </Row>

          <FileUploadWrapper>
            <FileUpload
              icon={<></>}
              style={{width: '100%'}}
              variant="secondary"
              onUpload={item => console.log(item)}
              note={<Typography variant="bodySmall" content="Dokaz o zaposlenju" />}
              buttonText="Učitaj"
            />
          </FileUploadWrapper>
        </FormWrapper>
      }
      title={'DODAJTE NOVO ZAPOSLENJE'}
    />
  );
};

import {Datepicker, Dropdown, Input, Modal} from 'client-library';
import React, {useEffect, useMemo} from 'react';
import {Controller, useForm} from 'react-hook-form';
import {nationalMinorities} from '../../constants';
import {FamilyMemberModalProps} from '../../screens/employees/family/types';
import {formatData} from '../../screens/employees/family/utils';
import useFamilyInsert from '../../services/graphql/userProfile/family/useFamilyInsert';
import {UserProfileFamilyParams} from '../../types/graphql/userProfileGetFamilyTypes';
import {cityData, employeeRelationshipDropdownData} from '../../utils/constants';
import {FormWrapper, Row} from './styles';
import {parseToDate} from '../../utils/dateUtils';

const initialValues: UserProfileFamilyParams = {
  id: 0,
  user_profile_id: 0,
  first_name: '',
  last_name: '',
  date_of_birth: null,
  country_of_birth: null,
  city_of_birth: '',
  nationality: null,
  citizenship: null,
  address: '',
  father_name: '',
  mother_name: '',
  mother_birth_last_name: '',
  official_personal_id: '',
  gender: '',
  insurance_coverage: '',
  handicapped_person: false,
  employee_relationship: '',
  national_minority: null,
};

export const FamilyMemberModal: React.FC<FamilyMemberModalProps> = ({
  selectedItem,
  open,
  onClose,
  countries,
  userProfileId,
  alert,
  refetch,
}) => {
  const {
    register,
    handleSubmit,
    control,
    watch,
    formState: {errors, dirtyFields},
    reset,
    resetField,
  } = useForm({defaultValues: initialValues});

  const {mutate, loading: isSaving} = useFamilyInsert();

  const country_of_birth = watch('country_of_birth');
  const countryIsMontenegro = country_of_birth?.id === 'mne';

  const countryOptions = useMemo(() => {
    return (
      countries?.map(country => {
        return {
          id: country.alpha3,
          title: country.name,
        };
      }) || []
    );
  }, [countries]);

  useEffect(() => {
    if (selectedItem) {
      reset({
        ...selectedItem,
        handicapped_person: {
          id: selectedItem?.handicapped_person ? 'Da' : 'Ne',
          title: selectedItem?.handicapped_person ? 'Da' : 'Ne',
        },
        insurance_coverage: {
          id: selectedItem?.insurance_coverage === 'Ne' ? 'Ne' : 'Da',
          title: selectedItem?.insurance_coverage === 'Ne' ? 'Ne' : 'Da',
        },
        employee_relationship: {id: selectedItem?.employee_relationship, title: selectedItem?.employee_relationship},
        country_of_birth: countryOptions?.find(country => country.id === selectedItem?.country_of_birth),
        citizenship: countryOptions?.find(country => country.id === selectedItem?.citizenship),
        gender: {id: selectedItem?.gender, title: selectedItem?.gender},
        date_of_birth: parseToDate(selectedItem?.date_of_birth),
        national_minority: nationalMinorities?.find(nm => nm.id === selectedItem.national_minority),
        nationality: countryOptions?.find(c => c.title === selectedItem.nationality),
        city_of_birth:
          selectedItem.country_of_birth !== 'mne'
            ? selectedItem.city_of_birth
            : cityData.find(city => city.id === selectedItem.city_of_birth),
      });
    }
  }, [selectedItem]);

  useEffect(() => {
    if (countryIsMontenegro && dirtyFields.country_of_birth) {
      resetField('city_of_birth', {defaultValue: ''});
    }
  }, [country_of_birth]);

  const onSubmit = (data: any) => {
    if (isSaving) return;

    const payload = formatData(data);
    mutate(
      {...payload, user_profile_id: userProfileId || 0},
      () => {
        alert.success('Uspješno sačuvano.');
        refetch();
        onClose();
      },
      () => {
        alert.error('Greška. Promjene nisu sačuvane.');
        onClose();
      },
    );
  };

  return (
    <Modal
      open={open}
      onClose={() => {
        onClose();
      }}
      leftButtonText="Otkaži"
      rightButtonText="Sačuvaj"
      rightButtonOnClick={handleSubmit(onSubmit)}
      buttonLoading={isSaving}
      content={
        <FormWrapper>
          <Row>
            <Controller
              name="employee_relationship"
              rules={{required: 'Ovo polje je obavezno'}}
              control={control}
              render={({field: {onChange, name, value}}) => {
                return (
                  <Dropdown
                    onChange={onChange}
                    value={value as any}
                    name={name}
                    label="SRODSTVO:"
                    options={employeeRelationshipDropdownData}
                    error={errors.employee_relationship?.message}
                  />
                );
              }}
            />
            <Controller
              name="country_of_birth"
              rules={{required: 'Ovo polje je obavezno'}}
              control={control}
              render={({field: {onChange, name, value}}) => {
                return (
                  <Dropdown
                    onChange={onChange}
                    value={value as any}
                    name={name}
                    isSearchable
                    label="DRŽAVA ROĐENJA:"
                    options={countryOptions}
                    error={errors.country_of_birth?.message}
                  />
                );
              }}
            />
            <Controller
              name="insurance_coverage"
              rules={{required: 'Ovo polje je obavezno'}}
              control={control}
              render={({field: {onChange, name, value}}) => {
                return (
                  <Dropdown
                    onChange={onChange}
                    value={value as any}
                    name={name}
                    label="KORISNIK OSIGURANJA:"
                    options={[
                      {id: 'Da', title: 'Da'},
                      {id: 'Ne', title: 'Ne'},
                    ]}
                    error={errors.insurance_coverage?.message}
                  />
                );
              }}
            />
            <Controller
              name="handicapped_person"
              rules={{required: 'Ovo polje je obavezno'}}
              control={control}
              render={({field: {onChange, name, value}}) => {
                return (
                  <Dropdown
                    onChange={onChange}
                    value={value as any}
                    name={name}
                    label="LICE SA INVALIDITETOM:"
                    options={[
                      {id: 'Da', title: 'Da'},
                      {id: 'Ne', title: 'Ne'},
                    ]}
                    error={errors.handicapped_person?.message}
                  />
                );
              }}
            />
          </Row>
          <Row>
            <Input
              {...register('first_name', {required: 'Ovo polje je obavezno'})}
              label="IME:"
              error={errors.first_name?.message}
            />
            <Controller
              name="citizenship"
              rules={{required: 'Ovo polje je obavezno'}}
              control={control}
              render={({field: {onChange, name, value}}) => {
                return (
                  <Dropdown
                    onChange={onChange}
                    value={value as any}
                    name={name}
                    label="DRŽAVLJANSTVO:"
                    options={countryOptions}
                    error={errors.citizenship?.message}
                    isSearchable
                  />
                );
              }}
            />
            <Controller
              name="nationality"
              control={control}
              render={({field: {onChange, name, value}}) => {
                return (
                  <Dropdown
                    onChange={onChange}
                    value={value as any}
                    name={name}
                    label="NACIONALNOST:"
                    options={countryOptions}
                    isSearchable
                  />
                );
              }}
            />
            <Controller
              name="national_minority"
              control={control}
              render={({field: {onChange, name, value}}) => {
                return (
                  <Dropdown
                    onChange={onChange}
                    value={value as any}
                    name={name}
                    label="NACIONALNA MANJINA:"
                    options={nationalMinorities || []}
                  />
                );
              }}
            />
          </Row>
          <Row>
            <Input
              {...register('last_name', {required: 'Ovo polje je obavezno'})}
              label="PREZIME:"
              error={errors.last_name?.message}
            />
            <Controller
              name="city_of_birth"
              rules={{
                validate: (value: any) =>
                  (!value && country_of_birth?.id === 'MNE') || country_of_birth?.title === null
                    ? 'Ovo polje je obavezno'
                    : true,
              }}
              control={control}
              render={({field: {onChange, name, value}}) => {
                return countryIsMontenegro ? (
                  <Dropdown
                    onChange={onChange}
                    value={value as any}
                    name={name}
                    label="OPŠTINA:"
                    options={cityData}
                    error={errors.city_of_birth?.message}
                    isSearchable
                  />
                ) : (
                  <Input
                    {...register('city_of_birth', {
                      validate: (value: any) =>
                        (!value && countryIsMontenegro) || country_of_birth?.title === ''
                          ? 'Ovo polje je obavezno'
                          : true,
                    })}
                    label="OPŠTINA:"
                    error={errors.city_of_birth?.message}
                  />
                );
              }}
            />

            <Input {...register('address')} label="ADRESA:" />
          </Row>
          <Row>
            <Controller
