                    <Dropdown
                      name={name}
                      value={value as any}
                      onChange={onChange}
                      options={yearOptions}
                      error={errors.planned_year?.message as string}
                      placeholder="Izaberite godinu"
                      label="GODINA:"
                    />
                  )}
                />
              </FormGroup>
              <FormGroup>
                <Controller
                  control={control}
