import React, {useMemo, useState} from 'react';
import {Norms} from '../../types/graphql/judges';
import {FilterDropdown, Filters, Header, OverviewBox} from '../judgesList/styles';
import {DropdownDataString} from '../../types/dropdownData';
import {judgeNormsTableHeads} from '../../screens/judges/judgeNorms/constants';
import {topicOptions} from '../../screens/judges/constants';

interface NormsListProps {
  data: Norms[];
  toggleNormsModal: (item: Norms) => void;
  handleDeleteIconClick: (id: number) => void;
  loading: boolean;
}

const NormsList: React.FC<NormsListProps> = ({data, toggleNormsModal, handleDeleteIconClick, loading}) => {
  const [topic, setTopic] = useState<DropdownDataString | null>(null);

  const onTopicChange = (value: any) => {
    if (value.id === 'Sve') {
      setTopic(null);
      return;
    }
    setTopic(value);
  };

  const list = useMemo(() => {
    const filteredData = topic ? data?.filter(norm => norm.topic === topic?.id) : data;
    return filteredData?.map(item => ({
      ...item,
      number_of_items: Number(item.title) - Number(item.title) * (Number(item.number_of_norm_decrease) / 100),
    }));
  }, [data, topic]);

  return (
    <OverviewBox>
      <Header>
        <Filters>
          <FilterDropdown
            label="MATERIJA:"
            options={topicOptions}
            value={topic}
            name="topic"
            onChange={value => onTopicChange(value)}
            placeholder="Odaberite materiju"
          />
        </Filters>
      </Header>
      <Table
        tableHeads={judgeNormsTableHeads}
        data={list}
        style={{marginBottom: 22}}
        isLoading={loading}
        tableActions={[
          {
            name: 'edit',
            onClick: item => toggleNormsModal(item),
            icon: <EditIconTwo stroke={Theme?.palette?.gray800} />,
          },
          {
            name: 'delete',
            onClick: item => handleDeleteIconClick(item.id),
            icon: <TrashIcon stroke={Theme?.palette?.gray800} />,
          },
        ]}
      />
    </OverviewBox>
  );
};

export default NormsList;

import {CheckIcon, Dropdown, Input, Modal, Theme} from 'client-library';
import React, {useEffect, useMemo} from 'react';
import {Controller, useForm} from 'react-hook-form';
import {FormGroup, ModalContentWrapper} from './styles';
import {OrganizationUnitModalProps} from './types';
import {OrganizationUnit} from '../../types/graphql/organizationUnitsTypes';
import useOrganizationUnitInsert from '../../services/graphql/organizationUnits/useOrganizationUnitInsert';

const initialValues: OrganizationUnit = {
  id: 0,
  parent_id: 0,
  number_of_judges: 0,
  title: '',
  abbreviation: '',
  description: '',
  address: '',
  color: '',
  folder_id: 0,
  icon: '',
};

export const OrganizationalUnitModal: React.FC<OrganizationUnitModalProps> = ({
  open,
  onClose,
  dropdownData,
  organizationUnit,
  selectedItem,
  alert,
  refetch,
}) => {
  const item = useMemo(() => {
    return selectedItem
      ? {
        ...selectedItem,
        parent_id: {id: organizationUnit?.id, title: organizationUnit?.title},
      }
      : {
        ...initialValues,
        parent_id: {id: organizationUnit?.id, title: organizationUnit?.title},
      };
  }, [selectedItem]);

  const {
    register,
    handleSubmit,
    control,
    formState: {errors},
    reset,
  } = useForm({defaultValues: item || initialValues});

  const {mutate, loading: isSaving} = useOrganizationUnitInsert();

  useEffect(() => {
    if (item) {
      reset(item);
    }
  }, [item]);

  const onSubmit = async (values: any) => {
    if (isSaving) return;
    const data = {
      ...values,
      parent_id: organizationUnit?.id,
      number_of_judges: values?.number_of_judges || 0,
      folder_id: values?.folder_id || 0,
    };

    delete data.job_positions_organization_units;

    try {
      mutate(
        data,
        () => {
          alert.success('Uspješno sačuvano.');
          refetch();
          onClose(true);
          reset(initialValues);
        },
        () => {
          alert.error('Greška. Promjena nije sačuvana.');
        },
      );
    } catch (e) {
      console.log(e);
    }
  };

  return (
    <Modal
      open={open}
      onClose={() => {
        onClose(false);
        reset(item);
      }}
      leftButtonText="Otkaži"
      rightButtonText="Sačuvaj"
      rightButtonOnClick={handleSubmit(onSubmit)}
      buttonLoading={isSaving}
      content={
        <ModalContentWrapper>
          <FormGroup>
