            placeholder="Odaberite godinu:"
          />
        </YearWrapper>
        <div>
          <Button
            variant="secondary"
            content={<Typography variant="bodyMedium" content="Dodajte svrhu" />}
            onClick={handleAdd}
          />
        </div>
      </TableHeader>

      <div>
        <Table
          tableHeads={tableHeads}
          data={filteredTableData || []}
          isLoading={loading}
          tableActions={[
