              }
            />
          );
        },
      },
      ...tableHeads.slice(3),
    ];
  }, [organizationUnits, tableHeads]);

  return (
    <Container>
      <span>
        <Button
          variant="secondary"
          content={<Typography variant="bodyMedium" content="Dodajte novo zaposlenje" />}
