              />
            </FormItem>
            <FormItem>
              <Controller
                name="organization_unit"
                control={control}
                render={({field: {name}}) => (
                  <Dropdown
                    name={name}
                    label="ORGANIZACIONA JEDINICA:"
                    isDisabled
                    value={profileData?.contract.organization_unit}
                    options={
                      profileData?.contract.organization_unit ? new Array(profileData?.contract.organization_unit) : []
                    }
                  />
                )}
              />
            </FormItem>
            <FormItem>
              <Controller
                name="benefited_track"
                control={control}
                render={({field: {onChange, name, value}}) => (
                  <Dropdown
                    name={name}
                    onChange={onChange}
                    label="BENEFI. STAŽ:"
                    isDisabled={isDisabled}
                    value={value}
                    options={yesOrNoOptionsString}
                  />
                )}
              />
            </FormItem>
            <FormItem>
              <Controller
                name="without_raise"
                control={control}
                render={({field: {onChange, name, value}}) => (
                  <Dropdown
                    name={name}
                    onChange={onChange}
                    label="BEZ POVEĆANJA:"
                    isDisabled={isDisabled}
                    value={value as any}
                    options={yesOrNoOptionsBoolean as any}
                  />
                )}
              />
            </FormItem>
            <FormItem>
              <Controller
                name="insurance_basis"
                control={control}
                render={({field: {onChange, name, value}}) => (
                  <Dropdown
                    name={name}
                    onChange={onChange}
                    label="OSNOVA OSIG. PO PIO:"
                    isDisabled={isDisabled}
                    value={value}
                    options={insuranceBasis}
                  />
                )}
              />
            </FormItem>
            <FormItem>
              <Controller
                name="salary_rank"
                control={control}
                render={({field: {onChange, name, value}}) => (
                  <Dropdown
                    name={name}
                    onChange={onChange}
                    label="TARIFNI PLATNI RAZRED:"
                    isDisabled={isDisabled}
                    value={value}
                    options={salaryRanks}
                  />
                )}
              />
            </FormItem>
            <FormItem>
              <Controller
                name="user_resolution_id"
                control={control}
                render={({field: {name}}) => (
                  <Dropdown
                    name={name}
                    label="VRSTA UGOVORA:"
                    isDisabled
                    value={profileData?.contract.contract_type}
                    options={profileData?.contract?.contract_type ? new Array(profileData.contract.contract_type) : []}
                  />
