              />
            </FormItem>
            <FormItem>
              <Controller
                name=""
                control={control}
                render={({field: {name}}) => (
                  <Datepicker
                    onChange={() => console.log('')}
                    name={name}
                    selected={parseToDate(profileData?.contract?.date_of_start || null)}
                    label="POČETAK RADNOG ODNOSA:"
