export interface UseJobTendersParams extends JobTendersListFilters {
  id?: number;
  page: number;
  size: number;
}

const useJobTenderOverview = ({page, size, id, organization_unit_id, active, type_id}: UseJobTendersParams) => {
  const [data, setData] = useState<JobTendersResponse['data']['jobTenders_Overview']>(initialState);
  const [loading, setLoading] = useState(true);

  const fetchJobTenders = async () => {
    const jobTenders: any = await GraphQL.jobTenderOverview({
      page,
      size,
      id: id ? id : 0,
      organization_unit_id: organization_unit_id ? organization_unit_id.id : 0,
      active: active ? active.id : true,
      type_id: type_id?.id || 0,
    });
    setData(jobTenders);
    setLoading(false);
  };

  useEffect(() => {
    fetchJobTenders();
  }, [page, size, id, active, organization_unit_id, type_id]);

  return {data, loading, refetch: fetchJobTenders};
};

export default useJobTenderOverview;

import {useEffect, useMemo, useState} from 'react';
import {GraphQL} from '..';
import {OrganizationUnit} from '../../../types/graphql/organizationUnitsTypes';
import {REQUEST_STATUSES} from '../../constants';

const useOrganizationUnitOverview = (props?: any, notParent?: boolean) => {
  const [organizationUnits, setOrganizationUnits] = useState<OrganizationUnit[]>([]);
  const organizationUnitsList = useMemo(() => {
    return [
      {id: 0, title: 'Sve organizacione jedinice'},
      ...organizationUnits.map(unit => {
        return {id: unit.id, title: unit.title};
      }),
    ];
  }, [organizationUnits]);

  const fetchOrganizationUnits = async () => {
    GraphQL?.organizationUnits().then(response => {
      if (response?.status === REQUEST_STATUSES.success) {
        delete response.message;
        delete response.status;
        if (notParent) response.items = response.items?.filter(item => !item.parent_id);
        setOrganizationUnits(response.items as OrganizationUnit[]);
      } else {
        alert(`Login failed due to error - ${response.message}`);
      }
    });
  };

  useEffect(() => {
    fetchOrganizationUnits();
  }, [props]);

  return {organizationUnits, organizationUnitsList, fetch: fetchOrganizationUnits};
};

export default useOrganizationUnitOverview;

import {useEffect, useState} from 'react';
import {GraphQL} from '..';
import {SettingsDropdown, SettingsDropdownOverviewParams} from '../../../types/graphql/settingsDropdownType';
import {DropdownDataNumber} from '../../../types/dropdownData';

const useSettingsDropdownOverview = ({entity, page, size, id}: SettingsDropdownOverviewParams) => {
  const [data, setData] = useState<SettingsDropdown[]>();
  const [options, setOptions] = useState<DropdownDataNumber[]>([]);
  const [loading, setLoading] = useState(true);

  const getSettingsDropdownOverview = async () => {
    const response = await GraphQL.settingsDropdownOverview({entity: entity, page: page, size: size, id: id});

    //Ask filip if we need array of items here
    const data = response?.items || null;

    setData(data || []);
    setOptions(data?.map((item: any) => ({title: item.title, id: item.id})) || []);
    setLoading(false);
  };

  useEffect(() => {
    getSettingsDropdownOverview();
  }, [entity, id, page, size]);

  return {data: data, loading, refetch: getSettingsDropdownOverview, options};
};

export default useSettingsDropdownOverview;

