import React, {ReactElement, ReactNode, forwardRef} from 'react';
import useAppContext from '../../context/useAppContext';
import {Container, StyledBreadcrumbs} from './styles';

interface ScreenWrapperProps {
  children: ReactNode;
}

export const ScreenWrapper = forwardRef<HTMLDivElement, ScreenWrapperProps>(({children}, ref) => {
  const context = useAppContext();

  const breadcrumbs = context?.breadcrumbs;

  const breadcrumbItems = breadcrumbs?.get();

  const navigate = context?.navigation?.navigate;

  const handleNavigation = (
    e: React.MouseEvent<HTMLAnchorElement, MouseEvent> | undefined,
    item?: {
      name: string;
      to: string;
      icon?: ReactElement;
    },
  ) => {
    e?.preventDefault();
    const newBreacrumbs = [...breadcrumbItems];
    const index = newBreacrumbs.findIndex((breadcrumb: any) => breadcrumb.name === item?.name);
    newBreacrumbs.splice(index + 1, newBreacrumbs.length - index);
    breadcrumbs.set(newBreacrumbs);
    navigate(item?.to);
  };

  return (
    <Container ref={ref}>
      <StyledBreadcrumbs items={breadcrumbItems} onClick={handleNavigation} />
      {children}
    </Container>
  );
});

ScreenWrapper.displayName = 'ScreenWrapper';

import {useCallback, useEffect} from 'react';

function useConfirmExit(confirmExit: () => boolean, when = true) {
  useEffect(() => {
    if (!when) {
      return;
    }

    const originalPushState = window.history.pushState;

    window.history.pushState = function (state: any, title: string, url?: string | null) {
      const result = confirmExit();
      if (result !== false) {
        originalPushState.apply(window.history, [state, title, url]);
      }
    };

    return () => {
      window.history.pushState = originalPushState;
    };
  }, [confirmExit, when]);
}

export function usePrompt(message: string, when = true) {
  useEffect(() => {
    if (when) {
      window.onbeforeunload = function () {
        return message;
      };
    }

    return () => {
      window.onbeforeunload = null;
    };
  }, [message, when]);

  const confirmExit = useCallback(() => {
    const confirm = window.confirm(message);
    return confirm;
  }, [message]);
  useConfirmExit(confirmExit, when);
}

